<div>	<h5>{# $mod_info[0].name #}</h5>		<script language="javascript" src="{# $mod_path #}/js/function.js"></script>		<div class="chooser-panel">		<ul>			<li><a href="{# $SESS_URL #}section=module">Вернуться к списку модулей</a></li>		</ul>	</div>	<br class="clear">	<div class="block-content" style="height: auto; width: 90%; padding-bottom: 50px;">		<h4>Список сообщений</h4>		<br>				{# if $Pages #}		<div class="pages">			{# foreach name=pages from=$Pages item=Item #}			{# if ($smarty.get.p==$Item) or (!isset($smarty.get.p) and $Item==1) #}			<span style="font-size: 10pt; font-family: Tahoma;">{# $Item #}</span>			{# else #}			<a href="{# $SESS_URL #}section=module&type={# $smarty.get.type #}&p={# $Item #}">{# $Item #}</a>			{# /if #}			{# if not $smarty.foreach.pages.last #}|{# /if #}			{# /foreach #}							</div>		<br>		{# /if #}				<table class="guestbook-message-table" style="width: 100%;">			<thead>				<tr>					<th class="num-col">&nbsp;</th>					<th>Дата</th>					<th>Имя</th>					<th>E-mail</th>					<th>Категория</th>					<th>Текст</th>					<th>Операции</th>				</tr>			</thead>			<tbody>				{# foreach from=$Messages key=Key item=Item #}				<tr class="{# if $Key is odd #}odd{# else #}even{# /if #}">					<td class="num-col">{# $Item.num #}</td>					<td>{# $Item.date|date_format:"%d.%m.%Y %H:%M:%S" #}</td>					<td>{# $Item.name #}</td>					<td>{# $Item.email #}</td>					<td>						<select class="message-cat">							{# foreach from=$Cats item=item #}							<option value="{# $item.id_cat #}"{# if $Item.id_cat == $item.id_cat #} selected{# /if #}>{# $item.name #}</option>							{# /foreach #}						</select>					</td>					<td>{# $Item.text #}</td>					<td>						<input type="hidden" class="message-id" name="id_message" value="{# $Item.id_message #}" />						{# if $Item.status == 0 #}						<button style="width: 120px;" class="guestbook-message-button-submit">Подтвердить</button><br />						{# /if #}						<button style="width: 120px;" class="guestbook-message-button-update">Сохранить</button><br />						<button style="width: 120px;" class="guestbook-message-button-delete">Удалить</button><br />					</td>				</tr>				{# /foreach #}			</tbody>		</table>				{# if $Pages #}		<br>		<div class="pages">			{# foreach name=pages from=$Pages item=Item #}			{# if ($smarty.get.p==$Item) or (!isset($smarty.get.p) and $Item==1) #}			<span style="font-size: 10pt; font-family: Tahoma;">{# $Item #}</span>			{# else #}			<a href="{# $SESS_URL #}section=module&type={# $smarty.get.type #}&p={# $Item #}">{# $Item #}</a>			{# /if #}			{# if not $smarty.foreach.pages.last #}|{# /if #}			{# /foreach #}							</div>		{# /if #}				<br class="clear">	</div></div>